import React, { useState } from "react";
import { Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { useHistory } from "react-router-dom";
import bcrypt from "bcryptjs"; // importa a biblioteca bcryptjs

import './Login.css';



export default function SignInPage() {
    const [firstname, setFirstname] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    const handleLogin = (e) => {
      e.preventDefault();
      
      const saltRounds = 10; // define o número de rounds de sal usados para criptografia
      const hashedPassword = bcrypt.hashSync(password, saltRounds); // criptografa a senha usando o bcryptjs

      const endpoint = `https://sngx3c6fe7.execute-api.sa-east-1.amazonaws.com/dev/login`;
      const params = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ firstname, password: hashedPassword }) // envia a senha criptografada para a API
      };
      fetch(endpoint, params)
        .then((res) => {
          if (res.status === 200) {
            // redireciona para a página de sucesso
            history.push("/");
            window.alert("ue");
          } else {
            // exibe uma mensagem de erro
            window.alert("Invalid credentials");
          }
        });
    }
  
    return (
      <div className="text-center m-5-auto">
        <h2>Praticando Saúde</h2>
        <form onSubmit={handleLogin}>
          <p>
            <label>Username or email address</label><br/>
            <input type="text" name="firstname" value={firstname} onChange={(e) => setFirstname(e.target.value)} required />
          </p>
          <p>
            <label>Password</label>
            <Link to="/forget-password"><label className="right-label">Forget password?</label></Link>
            <br/>
            <input type="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </p>
          <p>
            <button id="sub_btn" type="submit">Login</button>
          </p>
        </form>
        <footer>
          <p>First time? <Link to="/register">Create an account</Link>.</p>
        </footer>
      </div>
    );
}
