import React, { useEffect, useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  container: {
    marginTop: theme.spacing(2),
  },
  paper: {
    padding: theme.spacing(2),
    color: theme.palette.text.secondary,
  },
}));

const handleSubmit = event => {
  event.preventDefault();
  window.location.href = '/create';
}

export default function UserList() {
  const classes = useStyles();



  const [users, setUsers] = useState([]);
  useEffect(() => {
    UsersGet()
  }, [])

  
  const UsersGet = async () => {
    try {
      const response = await fetch("https://r4wj0utagf.execute-api.us-east-1.amazonaws.com/prod/dynamodb");
      const result = await response.json();
      const users = result.Items?.map(item => ({
        id: item.id.S,
        firstName: item.firstName.S,
        lastName: item.lastName.S,
        email: item.email.S,
        userName: item.userName.S,
        cpfCnpj: item.cpfCnpj.S
      })) ;
      setUsers(users); // adiciona essa linha para atualizar o estado com a lista de usuários
    } catch (error) {
      console.error('Error fetching data from API Gateway:', error);
    }
  }

  const UpdateUser = id => {
    window.location = '/update/'+id
  }

  

  const UserDelete = (id) => {
    fetch(`https://sua-api-gateway.execute-api.us-east-1.amazonaws.com/prod/deleteusuarios/${id}`, {
      method: 'GET',
    })
      .then((res) => res.json())
      .then((result) => {
        alert(result.message);
        if (result.status === 'ok') {
          UsersGet();
        }
      })
      .catch((error) => {
        console.error('Error deleting user:', error);
      });
  };

  

  return (
    <div className={classes.root}>
      <Container className={classes.container} maxWidth="lg">    
        <Paper className={classes.paper}>
          <Box display="flex">
            <Box flexGrow={1}>
              <Typography component="h2" variant="h6" color="primary" gutterBottom>
                USERS
              </Typography>
            </Box>
            <Box>
            <Button variant="contained" color="primary" type="button" onClick={handleSubmit}>
              CREATE
            </Button>
          </Box>
          </Box>
          <TableContainer component={Paper}>
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="right">ID</TableCell>
                <TableCell align="left">First</TableCell>
                
                <TableCell align="left">Last Name</TableCell>
                <TableCell align="left">Email</TableCell>
                <TableCell align="left">Username</TableCell>
                <TableCell align="left">CPF ou CNPJ</TableCell>
                <TableCell align="center">Action</TableCell>
                
                
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map((item) => (
                <TableRow key={item.id}>
                  <TableCell align="right">{item.id}</TableCell>
                  <TableCell align="left">{item.firstName}</TableCell>
                  <TableCell align="left">{item.lastName}</TableCell>
                  <TableCell align="left">{item.email}</TableCell>
                  <TableCell align="left">{item.userName}</TableCell>
                  <TableCell align="left">{item.cpfCnpj}</TableCell>
                  <TableCell align="center">
                    <ButtonGroup color="primary" aria-label="outlined primary button group">
                      <Button onClick={() => UpdateUser(item.id)}>Edit</Button>
                      <Button onClick={() => UserDelete(item.id)}>Del</Button>
                    </ButtonGroup>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        </Paper>
      </Container>
    </div>
    
  );
}
