import jwt_decode from 'jwt-decode';
import { useEffect, useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import ButtonGroup from '@material-ui/core/ButtonGroup';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  container: {
    marginTop: theme.spacing(2),
  },
  paper: {
    padding: theme.spacing(2),
    color: theme.palette.text.secondary,
  },
}));

const handleSubmit = event => {
  event.preventDefault();
  window.location.href = '/HomePage';
}
export default function HomePage() {
  const [user, setUser] = useState(null);
  const history = useHistory();
  const classes = useStyles();

  const [users, setUsers] = useState([]);
  useEffect(() => {
    UsersGet()
  }, [])



  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      // redireciona o usuário para a página de login se o token não estiver presente
      history.push('/');
    } else {
      try {
        // decodifica o token para obter o ID do usuário
        const { user_id } = jwt_decode(token);
        // atualiza o estado do usuário com o ID decodificado
        setUser({ id: user_id });
      } catch (error) {
        // redireciona o usuário para a página de login se o token for inválido
        history.push('/');
      }
    }
  }, [history]);

  if (!user) {
    // exibe uma mensagem de carregamento enquanto o usuário está sendo verificado
    return <div>Loading...</div>;
  }
  const UsersGet = async () => {
    try {
      const response = await fetch("https://r4wj0utagf.execute-api.us-east-1.amazonaws.com/prod/dynamodb");
      const result = await response.json();
      console.log(response);
      if (result && typeof result === 'object') {
        const user = {
          id: result.id,
          firstName: result.firstName,
          lastName: result.lastName,
          email: result.email,
          userName: result.userName,
          cpfCnpj: result.cpfCnpj
        };
        function compare(a, b) {
          if (a.id < b.id) {
            return -1;
          }
          if (a.id > b.id) {
            return 1;
          }
          return 0;
        }
        setUsers(result.map((user) => ({ ...user, key: user.id })).sort(compare));
        console.log(result)
      }else {
        console.error('API response is not an array or object:', result);
      }
    } catch (error) {
      console.error('Error fetching data from API Gateway:', error);
    }
    
    
  }

  return (
    <div>
      <h1>Welcome, User {user.id}!</h1>
      <p>This is your homepage.</p>
    </div>
  );
}
